CREATE SCHEMA lojamoveis;

USE lojamoveis;

CREATE TABLE Usuario (
	id_usuario INTEGER PRIMARY KEY,
	CPF VARCHAR(11),
    Nome VARCHAR(100),
    Endereco VARCHAR(100),
    Telefone VARCHAR(20),
    Email VARCHAR(320),
    Senha VARCHAR(255)
);

CREATE TABLE Pedido (
	id_pedido INTEGER PRIMARY KEY,
    valor_total FLOAT,
	previsao_entrega DATE,
	data_criado DATE,
    valor_frete FLOAT,
	forma_pagamento INTEGER CHECK (forma_pagamento >= 0 AND forma_pagamento <= 2), # 0=Credito    1=Boleto    2=PIX
    estado INTEGER CHECK (estado >= 0 AND estado <= 1), # 0=Em andamento    1=Finalizado
	id_usuario INTEGER,
    id_produto INTEGER,
    FOREIGN KEY (id_usuario) REFERENCES Usuario(id_usuario),
    FOREIGN KEY (id_produto) REFERENCES Produto(id_produto)
);

CREATE TABLE Produto (
	id_produto INTEGER PRIMARY KEY,
    nome_produto VARCHAR(100),
    valor_unitario DECIMAL(10, 2),
    Categoria VARCHAR(40),
    Cor VARCHAR(40),
    Descricao VARCHAR(400),
    Foto VARCHAR(255)
);

